mapper.select:
select dw.dwywlsh,dw.jjbh,dw.jsztbh,dw.dwbh,fse3,dw.fsrs,dw.zckfse,cjsj3,d.dwmc2,j.ztmc,h.jjmc from dwywmxb dw join dwzh d on d.dwbh=dw.dwbh join jsztb j on j.jsztbh=dw.jsztbh join hjfs h on h.jjbh=dw.jjbh

	
		and cjsj3 between #{date1} and #{date2}
	
	
		and d.dwmc2 like "%"#{dwmc2}"%"

select COALESCE(sum(fse3),0) from dwywmxb dw join dwzh d on d.dwbh=dw.dwbh join jsztb j on j.jsztbh=dw.jsztbh join hjfs h on h.jjbh=dw.jjbh

	
		and cjsj3 between #{date1} and #{date2}
	
	
		and d.dwmc2 like "%"#{dwmc2}"%"

select COALESCE(sum(fsrs),0) from dwywmxb dw join dwzh d on d.dwbh=dw.dwbh join jsztb j on j.jsztbh=dw.jsztbh join hjfs h on h.jjbh=dw.jjbh

	
		and cjsj3 between #{date1} and #{date2}
	
	
		and d.dwmc2 like "%"#{dwmc2}"%"

select tqbh,zgxm,dwmc2,tqlx,tqze,spsj,spzt2 from Sy_gj_tqspb

	
		and spsj between #{date1} and #{date2}
	
	
		and tqlx=#{tqlx}
	
	
		and spzt2=#{spzt2}

select COALESCE(sum(tqze),0) from Sy_gj_tqspb

	
		and spsj between #{date1} and #{date2}
	
	
		and tqlx=#{tqlx}
	
	
		and spzt2=#{spzt2}

select count(*) from Sy_gj_tqspb

	
		and spsj between #{date1} and #{date2}
	
	
		and tqlx=#{tqlx}
	
	
		and spzt2=#{spzt2}
